import React from 'react'
import PropTypes from 'prop-types'
import Card from '../card'

export default function Table({cards,flipped,handleClick}) {
	return (
		<div className="c-game-table">
			{cards.map((card) => (<Card 
          id={card.id}
          type={card.type}
          width={card.width}
          height={card.height}
          back={'https://vignette.wikia.nocookie.net/yugioh/images/e/e5/Back-EN.png/revision/latest/scale-to-width-down/330?cb=20100726082133'}
          front={'https://cdn.shopify.com/s/files/1/1677/4363/products/Blue-Eyes_White_Dragon_2_water_800x.jpg?v=1548037392'}
          flipped={flipped.includes(card.id)}
          handleClick={() => handleClick(card.id)}
        />)
			)}
		</div>
		)	
}

Table.propTypes = {
	cards: PropTypes.arrayOf(PropTypes.shape({})).isRequired,
	flipped: PropTypes.arrayOf(PropTypes.number).isRequired
}
