      setPlayer1(player1+1)
    } else {
      setPlayer2(player2+1)
    }    
    console.log(player1)
  }

  return (
    <main>
      <header>
      </header>
      <Table 
        cards={cards}
        flipped={flipped}
        handleClick={handleClick}
        disabled = {disabled}
        solved ={solved}
      /> 
    </main>
  );
}

export default App;
