import React, {useState,useEffect} from 'react';
import './App.css';
import Table from './component/table'; 
import ScoreCard from './component/scorecard';

import initializeDeck from './component/deck';

function App() {
  let player1 = 0
  let player2 = 0

  const [flipped,setFlipped] = useState([])
  const [cards, setCards] = useState([])
  const [turn,setTurn] = useState(true)

  const [solved, setSolved] = useState([])
  const [disabled, setDisabled] = useState(false)

  useEffect(()=> {
    setCards(initializeDeck())
  },[])

  const handleClick = (id)=> {
      if (flipped.length == 0 ) {
      setFlipped([id])
      setDisabled(false)
    } else  {

      if(sameCardClicked(id)) return
      setDisabled(true)
      setFlipped([flipped[0],id])

      if (cardsMatch(id)) {
        addPoint()
        setTimeout(resetCards,1000)
      } else {
        setTurn(!turn)
        setTimeout(resetCards,1000)
      }

    }
  }

  const resetCards =() => {      
        setFlipped([])
        setDisabled(false)
    }

  const cardsMatch = (id) => {
   const firstCard = cards.find((card) => card.id === flipped[0])
   const secondCard = cards.find((card) => card.id === id )
   if (firstCard.type === secondCard.type) {
    setSolved([...solved,secondCard.id,firstCard.id])
    return true 
   } 
  }

  const sameCardClicked = (id) => flipped.includes(id)

  const addPoint = () => {
    if (turn === true) {
      player1 = player1++
      console.log('player1 has '+player1+' points')
    } else {
      player2 = player2++
      console.log('player2 has '+player2+' points')
    }
  }


  return (
    <main>
      <header>
        <ScoreCard 
          player1={player1}
          player2={player2}
        />
      </header>
      <h1 className="title"> Card Matching Game</h1>
      <Table 
        cards={cards}
        flipped={flipped}
        handleClick={handleClick}
        disabled = {disabled}
        solved ={solved}
      /> 
    </main>
  );
}

export default App;
