import React from 'react'
import PropTypes from 'prop-types'
import './style.css'


export default function Scoreboard(players,turn) {
	return (
		<section className="c-score-table">
			<div className= {turn ? 'c-score-icon top' :  'c-score-icon bottom'}> </div>
			{	
			players.players.map((player) => (
				<div className = 'c-score-table__row '>
				<div> {player.name} </div>
				<div> {player.score} </div>
				<div> {player.winner ? 'You Win!' : ''} </div>
				</div>
				))
			}
		</section>
		)
}
