import React, {useState,useEffect} from 'react';
import './App.css';
import Table from './component/table'; 

import initializeDeck from './component/deck';

function App() {

  const [flipped,setFlipped] = useState([])
  const [cards, setCards] = useState([])

  const [solved, setSolved] = useState([])
  const [disabled, setDisabled] = useState(false)
  //const [player1,player2] = useState()

  useEffect(()=> {
    setCards(initializeDeck())
  },[])

  const handleClick = (id)=> {
      if (flipped.length == 0 ) {
      setFlipped([id])
      setDisabled(false)
    } else  {
      if(sameCardClicked(id)) return
      setFlipped([flipped[0],id])
      if (cardsMatch(id)) {
        setSolved([...solved,flipped[0],id])
        setFlipped([])
        setDisabled(false)
      }
    }
    //const test = cards.find((card) => card.id === id )
    //flippedCards.push(test)
    //console.log(flippedCards)

  }

  const cardsMatch = (id) => {
   const firstCard = cards.find((card) => flipped[0] === id)
   const secondCard = cards.find((card) => card.id === id )
   console.log (flipped[0].type)
   //console.log(firstCard)
   //console.log(secondCard)
   //console.log(firstCard === secondCard) 
  }

  const sameCardClicked = (id) => flipped.includes(id)


  return (
    <main>
      <h1 className="title"> Card Matching Game</h1>
      <Table 
        cards={cards}
        flipped={flipped}
        handleClick={handleClick}
        disabled = {disabled}
      /> 
    </main>
  );
}

export default App;
