import React from 'react'
var PropTypes = require('prop-types');
import './style.css'


export default function Card({handleClick,id,flipped,back,front,height,width}){
	return <div
	className = {'flip-container ${flipped ? "flipped" : "" }'}
	style = {{width,height}}
	onClick = {() => handleClick(id)}
	>

		<div className ='Flipper'>
			<img 
				style={{height,width}}
				className = {fliped ? 'front' : 'back'}
				src = {flipped ? front : back} />
		</div>
	</div>
}

Card.propTypes = {
	handleClick: Proptype.func.isRequired,
	id: Proptype.number.isRequired,
	flipped: Proptype.bool.isRequired,
	back: Proptype.string.isRequired,
	front: Proptype.string.isRequired,
	height: Proptype.number.isRequired,
	width: Proptype.number.isRequired
}
