import React, {useState,useEffect} from 'react';
import './App.css';
import Table from './component/table';

import initializeDeck from './component/deck';
import Scoreboard from './component/scoresboard';


function App() {

  const [flipped,setFlipped] = useState([])
  const [cards, setCards] = useState([])
  const [turn,setTurn] = useState(true)

  const [solved, setSolved] = useState([])
  const [disabled, setDisabled] = useState(false)

  const [scorecard,setScorecard] = useState([{name: 'Player1',score:0,id:1,active:true,winner:false,key:1},{name: 'Player2',score:0,id:2,active:false,winner:false,key:2}])


  useEffect(()=> {
    setCards(initializeDeck())
  },[])

  const handleClick = (id)=> {
      if (flipped.length === 0 ) {
      setFlipped([id])
      setDisabled(false)
    } else  {

      if(sameCardClicked(id)) return
      setDisabled(true)
      setFlipped([flipped[0],id])

      if (cardsMatch(id)) {
        setTimeout(resetCards,1000)
      } else {
        setTurn(!turn)
        setTimeout(resetCards,1000)
      }

    }
  }

  const resetCards =() => {      
        setFlipped([])
        setDisabled(false)
    }

  const cardsMatch = (id) => {
   const firstCard = cards.find((card) => card.id === flipped[0])
   const secondCard = cards.find((card) => card.id === id )
   if (firstCard.type === secondCard.type) {
    setSolved([...solved,secondCard.id,firstCard.id])
    changeScore(turn)
    return true 
   } 
  }

  const sameCardClicked = (id) => flipped.includes(id)

  const changeScore = (turn) => {
    var index = turn ? 0 : 1
    setScorecard([scorecard.score++,index])
  }





  return (
    <main>
      <header>
        <Scoreboard 
          players = {scorecard}
        />
      </header>
      <Table 
        cards={cards}
        flipped={flipped}
        handleClick={handleClick}
        disabled = {disabled}
        solved ={solved}
      /> 
    </main>
  );
}

export default App;
