import React, {useState} from 'react';
import logo from './logo.svg';
import './App.css';
import Card from './component/card'; 

function App() {
  const [flipped,setFlipped] = useState([])

  const handleClick = (id)=> {
    setFlipped([id])
  } 
  return (
    <main>
      <h1 class="title"> Card Matching Game</h1>
      <div class = "table">
        <Card 
          id={1}
          width={750}
          height={1050}
          back={'https://vignette.wikia.nocookie.net/yugioh/images/e/e5/Back-EN.png/revision/latest/scale-to-width-down/330?cb=20100726082133'}
          front={'https://cdn.shopify.com/s/files/1/1677/4363/products/Blue-Eyes_White_Dragon_2_water_800x.jpg?v=1548037392'}
          flipped={flipped.includes(1)}
          handleClick={() => handleClick(1)}
        />
      </div>  
    </main>
  );
}

export default App;
